mapper.select:
SELECT

FROM tb_staff_user
WHERE
  user_name = #{userName}
AND
  pass_word = #{passWord}

SELECT 

FROM tb_staff_user


    real_name = #{realName}


    AND mobile = #{mobile}


    AND role_id = #{roleId}


    AND dept_id = #{deptId}

mapper.update:
UPDATE tb_staff_user
SET
  role_id = #{roleId},
  role_name = #{roleName}
WHERE
  role_id = #{roleId}

UPDATE tb_staff_user
SET
  dept_id = #{deptId},
  dept_name = #{deptName}
WHERE
  dept_id = #{deptId}
